<?phpnamespace App\Controller;use App\Entity\Book;use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;use Symfony\Component\HttpFoundation\Response;use Symfony\Component\Routing\Annotation\Route;class CatalogueController extends AbstractController{    /**     * @Route("/catalogue", name="catalogue")     * @return Response     */    public function readAll(): Response    {        $repo = $this->getDoctrine()->getRepository(Book::class);        $books = $repo->findAll();        return $this->render('catalogue/catalogue.html.twig', [            'books' => $books        ]);    }}