<?phpnamespace App\Controller;use App\Entity\Staff;use App\Form\StaffType;use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;use Symfony\Component\HttpFoundation\Request;use Symfony\Component\HttpFoundation\Response;use Symfony\Component\Routing\Annotation\Route;class StaffController extends AbstractController{    /**     * @Route ("/create-staff", name="create_staff")     * @param Request $request     * @return Response     */    public function create(Request $request): Response    {        $staff = new Staff();        $form = $this->createForm(StaffType::class, $staff);        // Va permettre d'hydrater l'objet lors de la soumission        $form->handleRequest($request);        // Is valid fait réf aux différentes contrintes de validation        if ($form->isSubmitted() && $form->isValid()) {            // Récupère l'objet doctrine et l'entity manager pour mettre à jour la bdd            dump($staff);/*            $em = $this->getDoctrine()->getManager();            $em->persist($staff);*/        }        return $this->render("Staff/create.html.twig", [            "form" => $form->createView()        ]);    }}