<?phpnamespace App\EventSubscriber;use App\Entity\Book;use DateTime;use EasyCorp\Bundle\EasyAdminBundle\Event\BeforeEntityPersistedEvent;use Symfony\Component\EventDispatcher\EventSubscriberInterface;class EasyAdminSubscriber implements EventSubscriberInterface{    public static function getSubscribedEvents(): array    {        // TODO: Implement getSubscribedEvents() method.        return [          BeforeEntityPersistedEvent::class => ['setUpdateAt']        ];    }    public function setUpdateAt(BeforeEntityPersistedEvent $event)    {        $entity = $event->getEntityInstance();        if (!($entity instanceof Book)) {            return;        }        $now = new DateTime('now');        $entity->setUpdatedAt($now);    }}