<?phpnamespace App\Form;use App\Entity\Admin;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\Extension\Core\Type\EmailType;use Symfony\Component\Form\Extension\Core\Type\PasswordType;use Symfony\Component\Form\Extension\Core\Type\TextType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolver;class AdminType extends AbstractType{    public function buildForm(FormBuilderInterface $builder, array $options)    {        parent::buildForm($builder, $options); // TODO: Change the autogenerated stub;        $builder            ->add("firstname", TextType::class, ["label" => "Entrez votre prénom"])            ->add("lastname", TextType::class, ["label" => 'Entrez votre nom de famille'])            ->add("username", EmailType::class, ["label" => 'Entrez votre  nom d\'utilisateur'])            ->add("password", PasswordType::class, ["label" => "Entrez un mot de passe"]);    }    // On précise que l'on fait réf à la classe    public function configureOptions(OptionsResolver $resolver)    {        parent::configureOptions($resolver); // TODO: Change the autogenerated stub        $resolver->setDefaults([            "data_class" => Admin::class        ]);    }}