<?phpnamespace App\Form;use App\Entity\Book;use App\Entity\Catalogue;use Symfony\Bridge\Doctrine\Form\Type\EntityType;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\Extension\Core\Type\ChoiceType;use Symfony\Component\Form\Extension\Core\Type\DateType;use Symfony\Component\Form\Extension\Core\Type\FileType;use Symfony\Component\Form\Extension\Core\Type\TextareaType;use Symfony\Component\Form\Extension\Core\Type\TextType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolver;class BookType extends AbstractType{    public function buildForm(FormBuilderInterface $builder, array $options)    {        /*parent::buildForm($builder, $options); // TODO: Change the autogenerated stub*/        $builder            ->add("title", TextType::class, ["label" => "Entrez le titre"])            ->add("description", TextareaType::class, ["label" => 'Entrez la description'])            ->add("publication_date", DateType::class, ["label" => 'Entrez la date crÃ©ation'])            ->add("author", TextType::class, ["label" => 'Entrez le nom de l\'auteur'])            ->add("genre", TextType::class, ["label" => "Entrez un genre"])            ->add("picture", FileType::class, ["label" => "Ajoutez une image"])            ->add("catalogue", EntityType::class, [                'class' => Catalogue::class,                'choice_label' => function ($catalogue) {                return $catalogue->getCatalogueName();                }            ]);    }    public function configureOptions(OptionsResolver $resolver)    {/*        parent::configureOptions($resolver); // TODO: Change the autogenerated stub*/            $resolver->setDefaults([                "data_class" => Book::class            ]);    }}