<?phpnamespace App\Form;use App\Entity\Borrower;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\Extension\Core\Type\DateType;use Symfony\Component\Form\Extension\Core\Type\EmailType;use Symfony\Component\Form\Extension\Core\Type\PasswordType;use Symfony\Component\Form\Extension\Core\Type\TextType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolver;class BorrowerType extends AbstractType{    public function buildForm(FormBuilderInterface $builder, array $options)    {        /*parent::buildForm($builder, $options); // TODO: Change the autogenerated stub;*/        $builder            ->add("firstname", TextType::class, ["label" => "Entrez votre prénom"])            ->add("lastname", TextType::class, ["label" => 'Entrez votre nom de famille'])            ->add("email", EmailType::class, ["label" => 'Entrez votre  email'])            ->add("date_of_birth", DateType::class, ["label" => 'Entrez votre date de naissance'])            ->add("address", TextType::class, ["label" => "Entrez votre adresse"])            ->add("password", PasswordType::class, ["label" => "Entrez un mot de passe"]);    }    // On précise que l'on fait réf à la classe    public function configureOptions(OptionsResolver $resolver)    {        /*parent::configureOptions($resolver); // TODO: Change the autogenerated stub*/        $resolver->setDefaults([            "data_class" => Borrower::class        ]);    }}