<?phpnamespace App\Security;use App\Entity\Borrower as AppBorrower;use Symfony\Component\Security\Core\Exception\AccountExpiredException;use Symfony\Component\Security\Core\Exception\CustomUserMessageAccountStatusException;use Symfony\Component\Security\Core\User\UserCheckerInterface;use Symfony\Component\Security\Core\User\UserInterface;class UserChecker implements UserCheckerInterface{    public function checkPreAuth(UserInterface $user): void    {        if (!$user instanceof AppBorrower) {            return;        }        //$dump = dump($user->getIsChecked());        //$dump();        if ($user->getIsChecked() === false) {            throw new CustomUserMessageAccountStatusException("            Votre compte n\'est pas encore activÃ©" );        }    }    public function checkPostAuth(UserInterface $user)    {        if (!$user instanceof AppBorrower) {            return;        }        // user account is expired, the user may be notified        if ($user->getIsChecked() === false) {            throw new AccountExpiredException('...');        }    }}